function _Array__apply(method, args) {
    var array_prototype = Array.prototype;

    return array_prototype[method].apply(args[0], array_prototype.slice.call(args, 1));
}

/* exported Array__each */
function Array__each() {
    return _Array__apply('forEach', arguments);
}

/* exported Array__slice */
function Array__slice() {
    return _Array__apply('slice', arguments);
}

/* exported Array__splice */
function Array__splice() {
    return _Array__apply('splice', arguments);
}

/* exported Array__sort */
function Array__sort() {
    // 注意：
    // 标准里面，compare的返回值必须为Number
    // 但是chrome默认也识别bool, safari仅支持标准
    return _Array__apply('sort', arguments);
}

