/* exported Array__shuffle */
function Array__shuffle(o) {
    for (var j, x, i = o.length; i; j = Math.floor(Math.random() * i), x = o[--i], o[i] = o[j], o[j] = x);
    return o;
}

/* exported Array__remove */
function Array__remove(array, item) {
    var index = Array__indexOf(array, item);
    if (index === -1) {
        return array;
    }

    // Won't change origin array
    return Array__concat(Array__slice(array, 0, index), Array__slice(array, index + 1));
}

/* exported Array__has */
function Array__has(array, item) {
    return Array__indexOf(array, item) >= 0;
}
