var _LocalStorage__prefix = '';

/* exported LocalStorage__setPrefix */
function LocalStorage__setPrefix(prefix) {
    _LocalStorage__prefix = prefix;
}

/* exported LocalStorage__get */
function LocalStorage__get(key) {
    return localStorage[_LocalStorage__prefix + key];
}

/* exported LocalStorage__set */
function LocalStorage__set(key, value) {
    localStorage[_LocalStorage__prefix + key] = value;
}

